import React, { Component } from 'react';

import Strapi from 'strapi-sdk-javascript/build/main';
const apiUrl = process.env.APU_URL || 'http://localhost:1337';
const strapi = new Strapi(apiUrl);


class App extends Component {

  state = {
    brands: []
  }

  async componentDidMount() {

    const response = await strapi.request('POST', '/graphql', {
      data: { query: data }
    });

    this.setState(() => ({ brands: [ ...response.data.brands ] }))
    console.log(response.data.brands, "Respone from GraphQl");
  }

  showBrands = (brands) => (
    brands.map( brand => ( 
      <div key={brand._id}>
        <h2>{brand.name}</h2>
        <p>{brand.description}</p>
        <img src="brand.img." alt=""/>
      </div>))
  )


  render() {

    console.log(this.state.brands);

    return (
      <div className="container">
        <div className="jumbotron jumbotron-fluid mt-3">
          <div className="container">
            <h1 className="display-4">Brands</h1>
            <p className="lead">Check out our Brands Collection.</p>
          </div>
      </div>
      { this.state.brands.length > 0  ? this.showBrands(this.state.brands) : <p>Loading...</p>}
      </div>
    )
  }
}

export default App;

const data = `query {
  brands {
    _id
    name
    description
    createdAt
    image {
      name
      url
    }
  }
}`;
