import React, { Component } from 'react';
import { strapi, apiUrl } from '../Api/Strapi';
import { Link } from 'react-router-dom';

function Search() {
  return(
    <form className="mb-3">
        <input type="text" class="form-control" placeholder="Search" />
    </form>
  );
}

class App extends Component {

  state = {
    brands: []
  }

  async componentDidMount() {

    try {

      const response = await strapi.request('POST', '/graphql', {
        data: { query: data }
      });

      this.setState(() => ({ brands: [ ...response.data.brands ] }))
      console.log(response.data.brands, "Respone from GraphQl");


    } catch (err) {

      console.error('GRAPHQL GET BRAND ERROR: ', err);

    }

  }

  handle

  showBrands = (brands) => (
    brands.map( brand => ( 
      <div key={brand._id}>
        <h2>{brand.name}</h2>
        <p>{brand.description}</p>
        <a className="read-more" href={`/${brand._id}`}>More...</a>
        <img src={`${apiUrl}${brand.image.url}`} className="img-fluid" alt={`${brand.name} image`}/>
      </div>))
  )


  render() {

    console.log(this.state.brands);

    return (
      <div className="container">
        <div className="jumbotron jumbotron-fluid mt-3">
          <div className="container">
            <h1 className="display-4">Brands</h1>
            <p className="lead">Check out our Brands Collection.</p>
          </div>
      </div>
      <Search />
      { this.state.brands.length > 0  ? this.showBrands(this.state.brands) : <p>Loading...</p>}
      </div>
    )
  }
}

export default App;

const data = `query {
  brands {
    _id
    name
    description
    image {
      url
    }
  }
}`;
